INSERT INTO `WEB_IMAGE` VALUES ('6c0dda5e36b3450f8e70e9e60a017b73', '应用场景', '', '20181126/d9e5980fbb1240c6b2581a185a40df78.png', '', '6', '2', '本方案适用于为传统的智能硬件做人机语音交互赋能，为原来需要触摸和按键近距离交互的设备增加了远场非接触式的交互方式。方案可以应用于智能音箱、智能空调、智能照明、蓝牙音箱、蓝牙耳机、车载语音支架、智能开关、公共安全等多种产品与场景。', '15139b4d3593486a9a8bcc907b99ca66', '详情页2.png', null, '#000000');
