INSERT INTO `WEB_NEWMESSAGE` VALUES ('442bdec9c06644c4824428ab3dc848f1', '355b7c8ff650463bb9ef0907938da20a', null, '', '英飞凌社区“发帖回帖”有 “壕”礼 寻找你身边的学霸！', '', '8ae2e0a491b348e9b8ca17b8d0c18b69', '1', '14eb80d9f9bd47719b974245ab7a60b4', '<p style=\"text-align:center;\"><span style=\"font-size:24px;\"><strong><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"><span style=\"font-size:14px;\"></span></span></span></span></span></span></span></span></span></span></span></span></span></span><span style=\"font-size:18px;\">金秋10月社区</span></strong></span><span><span style=\"font-size:18px;\"><strong>新玩法：为鼓励大家好好学习！天天向上！</strong></span><img src=\"https://www.infineon-power-rf.org/Public/Js/Plug/kindeditor/ke4/plugins/emoticons/images/30.gif\" alt=\"\" border=\"0\"/><span style=\"font-size:18px;\"><strong>特举办学霸争夺赛，</strong></span></span><span><span style=\"font-size:18px;\"><strong>你是学霸？学渣？ or 学神？</strong></span><img src=\"https://www.infineon-power-rf.org/Public/Js/Plug/kindeditor/ke4/plugins/emoticons/images/44.gif\" alt=\"\" border=\"0\"/></span> </p><p style=\"text-align:center;\"><span><span style=\"font-size:18px;\"><strong>学霸之魂等你来解除封印！</strong></span><img src=\"https://www.infineon-power-rf.org/Public/Js/Plug/kindeditor/ke4/plugins/emoticons/images/42.gif\" alt=\"\" border=\"0\"/></span> </p><p style=\"text-align:left;\"><span style=\"font-size:24px;\"><span style=\"font-size:16px;\"></span><br/></span> </p><p style=\"text-align:left;\"><br/></p><p style=\"text-align:left;\"><span style=\"font-size:16px;\">活动时间：</span> </p><p style=\"text-align:left;\"><span style=\"font-size:18px;\"><strong><span style=\"font-size:16px;\">2017年10月10日--10月31日</span></strong><span style=\"font-size:16px;\">&nbsp;</span></span><span style=\"font-size:16px;\">持续进行~三周为一个周期总结~~</span><span style=\"font-size:24px;\"><br/></span> </p><p><span style=\"font-size:16px;\">参与对象：</span> </p><p><span style=\"font-size:16px;\">所有的攻城狮，广大的新老会员们~</span> <br/><span style=\"font-size:16px;\">参与方式：</span> </p><p><span style=\"font-size:18px;\"><strong><span style=\"font-size:16px;\">发帖</span></strong><span style=\"font-size:16px;\">、</span><strong><span style=\"font-size:16px;\">回帖</span></strong><span style=\"font-size:16px;\">两种方式</span></span> </p><p><strong><span style=\"color:#E53333;font-size:16px;\">发帖</span></strong><span><span style=\"color:#E53333;font-size:16px;\"><strong>部分</strong></span><span style=\"color:#E53333;font-size:16px;\"><strong>——</strong></span><span style=\"color:#E53333;font-size:16px;\"><strong>每天参与技术论坛版区发表问题求助，发表讨论等均可，参与数量+截图到本帖楼层~~</strong></span></span> </p><p><strong><span style=\"font-size:16px;\">参与地址：<a href=\"https://www.infineon-power-rf.org/bbs\">https://www.infineon-power-rf.org/bbs</a><br/></span></strong> </p><p><strong><span style=\"font-size:16px;\">在</span><span style=\"font-size:16px;\">英飞凌社区的电源管理、射频功放，射频与传感器，方案需求，活动专区，第三届DIY大赛任意版区发布与英飞凌相关的帖子，帖子类型可以为原创帖、讨论帖或问题帖（若已解决的问题帖，需写出解决方法）或者分享与英飞凌相关的资料帖（上传资料不能为下载中心板块已有的资料</span></strong><span style=\"font-size:16px;\"><strong>）</strong></span> </p><p><span style=\"color:#E53333;\"><strong><span style=\"font-size:16px;\">回</span><span style=\"font-size:16px;\">帖</span><span style=\"font-size:16px;\">部分</span></strong></span><span style=\"color:#E53333;\"><strong><span style=\"font-size:16px;\">——</span></strong><span style=\"font-size:16px;\"><strong>每天参与技术论坛版区跟帖交流，指导技术问题等均可，回复数量+截图到本帖楼层~~</strong></span></span> </p><p><strong><span style=\"font-size:16px;color:#000000;\">参与地址：<a href=\"https://www.infineon-power-rf.org/bbs\">https://www.infineon-power-rf.org/bbs</a></span></strong><span style=\"color:#E53333;\"></span><br/><span style=\"color:#EE33EE;\"><strong><span style=\"color:#000000;font-size:16px;\">（类似学习了，支持等回复灌水贴均不作评奖资格，谢谢理解）</span></strong><img src=\"https://www.infineon-power-rf.org/Public/Js/Plug/kindeditor/ke4/plugins/emoticons/images/66.gif\" alt=\"\" border=\"0\"/></span><br/><span style=\"font-size:16px;\">活动规则：</span> </p><p><span style=\"font-size:18px;\"><span style=\"font-size:16px;\">1、三周一轮回，周五总结，选出</span><strong><span style=\"font-size:16px;\">3</span></strong><span style=\"font-size:16px;\">名</span><strong><span style=\"font-size:16px;\">学霸</span></strong><span style=\"font-size:16px;\">入榜，</span><strong><span style=\"font-size:16px;\">5-10</span></strong><span style=\"font-size:16px;\">名热心参与者。</span></span><br/><span style=\"font-size:16px;\">2、为保证大家都有机会获奖，每周产生不同的学霸名额~</span><br/><span style=\"font-size:16px;\">3、发帖数量相同则以帖子回复数量取优，回贴则以最佳人气取优~</span><br/><br/><span style=\"font-size:16px;\">领奖方式</span><span style=\"font-size:16px;\">：</span> </p><p><span><span><span style=\"font-size:16px;\">获奖名单</span><span style=\"color:#EE33EE;font-size:16px;\"><strong>周</strong></span><span style=\"color:#EE33EE;font-size:16px;\"><span style=\"color:#EE33EE;font-size:16px;\"><strong>五公布</strong></span></span></span><span style=\"font-size:16px;\">本帖</span><span style=\"font-size:16px;\">，获奖的伙伴私信联系小编：21电源恬恬：QQ973919678</span></span> </p><p><span style=\"font-size:18px;\"><strong><span style=\"color:#000000;font-size:16px;\">（ps:公布学霸榜7日后，不联系小编的视为弃权奖品哦~</span></strong><span style=\"color:#000000;font-size:16px;\">）</span></span> </p><p style=\"text-align:center;\"><br/></p><p><strong><strong><span style=\"color:#E53333;font-size:24px;\">幸运学霸奖品：（任选其一）<span style=\"color:#000000;\"></span></span></strong></strong><span style=\"color:#E53333;\"><span style=\"color:#000000;\"><span>具体以实物为准</span></span></span> </p><p><br/></p><p><strong><br/></strong> </p><p><strong><br/></strong> </p><p><strong><a href=\"https://www.infineon-power-rf.org/Uploads/20171010/59dc7fd1921d1.png\"><img src=\"htt