INSERT INTO `WEB_NEWMESSAGE` VALUES ('9ac5c672a6d94dfabce9d2e31eb4e925', '355b7c8ff650463bb9ef0907938da20a', null, '', '英飞凌射频解决方案助力实现快速、高校、可靠的5G系统', '', '210d528fa7734acfbfeef4bc342629ef', '1', null, '<div id=\"edit\" class=\"con_textarea_news\" style=\"font-size: 14px;\"><p class=\"18\" style=\"font-size: 14px;\" align=\"justify\">2017<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">年</span><span style=\"font-family: Arial; font-size: 14px;\">3</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">月</span><span style=\"font-family: Arial; font-size: 14px;\">14</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">日，德国慕尼黑讯 </span><span style=\"font-family: Arial; font-size: 14px;\">– </span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">由于即将到来的</span><span style=\"font-family: Arial; font-size: 14px;\">5G</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">网络具有空前的规模、速度和复杂性，它将彻底改变不同规模企业开展业务的方式。</span><span style=\"font-family: Arial; font-size: 14px;\">5G</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">技术将为现有和新兴市场创造新机遇，同时使家居、城市、汽车和行业提高智能化、自动化和互联化水平。</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">然而</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">，实现这一颠覆性变革仍然面临着严峻的挑战：超高带宽、低至</span>1<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">毫秒的延迟和高度可靠的连接。另外，射频（</span><span style=\"font-family: Arial; font-size: 14px;\">RF</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">）架构必须可扩展、高效、可靠和极其紧凑。</span></p><p class=\"18\" style=\"font-size: 14px;\" align=\"justify\">&nbsp;</p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">立足半个世纪的系统能力，英飞凌科技股份公司（</span>FSE: IFX / OTCQX: IFNNY<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">）凭借其</span><span style=\"font-family: Arial; font-size: 14px;\">4G</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">技术经验，同时通过提供领先的射频技术和关键基础技术使移动基础设施和移动设备的</span><span style=\"font-family: Arial; font-size: 14px;\">5G</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">愿景成为可能。英飞凌提供</span><span style=\"font-family: Arial; font-size: 14px;\">5G</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">系统需要的高效集成架构。</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">作为市场上唯一一家拥有最丰富的</span>5G<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">产品系列的制造商，</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">英飞凌拥有：</span></p><p style=\"font-size: 14px;\">&nbsp;</p><ul style=\"font-size: 14px;\"><li style=\"font-size: 14px;\"><p><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">高效射频功率技术</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">，</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">即面向</span>6 GHz <span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">以上集成架构的</span>GaN-on-SiC<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">和</span>GaN-on-Si<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">技术，</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">以及面向</span>6GHz<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">以下系统具有</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">价格竞争力和高坚固性的</span>LDMOS<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">技术</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">。封装技术创新使宽带集成</span>Doherty<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">放大器成为可能</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">。</span></p></li></ul><p style=\"font-size: 14px;\">&nbsp;</p><ul style=\"font-size: 14px;\"><li style=\"font-size: 14px;\"><p><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">面向移动和低功率应用为基础的灵活射频解决方案，包括</span>SiGe<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">、</span><span style=\"font-family: Arial; font-size: 14px;\">BiCMOS</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">、</span><span style=\"font-family: Arial; font-size: 14px;\">GaN mmW</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">技术和</span><span style=\"font-family: Arial; font-size: 14px;\">RF CMOS</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">。它们可满足优化性能要求，同时提供备选方案，包括从成本优化设计到高集成度需求。</span></p></li></ul><p class=\"17\" style=\"font-size: 14px;\" align=\"justify\">&nbsp;</p><ul style=\"font-size: 14px;\"><li style=\"font-size: 14px;\"><p><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">面向频率高达</span>100 GHz <span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">的可扩展</span><span style=\"font-family: Arial; font-size: 14px;\">mmW</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">解决方案，在设计阶段给予客户极大的灵活性，帮助他们缩短产品上市时间。</span></p></li></ul><p style=\"font-size: 14px;\">&nbsp;<a href=\"https://www.infineon-power-rf.org/Uploads_old/20170315/58c8e9b0b67f5.png\" target=\"_blank\" style=\"font-size: 14px;\"><img style=\"padding: 5px; font-size: 14px;\" src=\"/uploadFiles/image/uploadFiles/image.png\" alt=\"\" _src=\"/uploadFiles/image/uploadFiles/image.png\"/></a></p><p style=\"font-size: 14px;\"><span style=\"font-size: 14px;\">Basestation Antenna Cellular</span></p><p style=\"font-size: 14px;\"><span style=\"font-size: 14px;\"><a href=\"https://www.infineon-power-rf.org/Uploads_old/20170315/58c8e9f689683.png\" target=\"_blank\" style=\"font-size: 14px;\"><img style=\"padding: 5px; font-size: 14px;\" src=\"https://www.infineon-power-rf.org/Uploads_old/20170315/58c8e9f689683_small.png\" alt=\"\"/></a></span></p><p style=\"font-size: 14px;\"><span style=\"font-size: 14px;\"><span style=\"font-size: 14px;\">Infographic 5G_e</span></span></p><p style=\"font-size: 14px;\">&nbsp;</p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">英飞凌已交付超过</span>2000<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">万颗采用波束成形技术的</span><span style=\"font-family: Arial; font-size: 14px;\">mmW</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">雷达收发器，是公认的车载雷达领域创新技术的领导者。英飞凌正为实现最高空间波束精度的相移技术铺平道路，使大规模</span><span style=\"font-family: Arial; font-size: 14px;\">MIMO</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">系统和基于多阵列的系统成为可能。同时，英飞凌继续投入宏基站、小基站、回程和终端用户设备等网络领域。</span></p><p style=\"font-size: 14px;\">&nbsp;</p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">大批量生产和无缝供应、高质量和高可靠性优势与英飞凌旨在实现</span>5G<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">愿景的整体解决方案相得益彰。</span></p><p style=\"font-size: 14px;\">&nbsp;</p><p style=\"font-size: 14px;\"><strong style=\"font-size: 14px; font-weight: bold;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">关于英飞凌</span></strong></p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">英飞凌科技股份公司是全球领先的半导体科技公司，我们让人们的生活更加便利、安全和环保。英飞凌的微电子产品和解决方案将带您通往美好的未来。</span>2016<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">财年（截止</span><span style=\"font-family: Arial; font-size: 14px;\">9</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">月</span><span style=\"font-family: Arial; font-size: 14px;\">30</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">日），公司的销售额达</span><span style=\"font-family: Arial; font-size: 14px;\">65</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">亿欧元，在全球范围内拥有约</span><span style=\"font-family: Arial; font-size: 14px;\">36,300</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">名员工。英飞凌在法兰克福证券交易所（股票代码：</span><span style=\"font-family: Arial; font-size: 14px;\">IFX</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">）和美国柜台交易市场 </span><span style=\"font-family: Arial; font-size: 14px;\">OTCQX International Premier</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">（股票代码：</span><span style=\"font-family: Arial; font-size: 14px;\">IFNNY</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">）挂牌上市。</span></p><p style=\"font-size: 14px;\">&nbsp;</p><p style=\"font-size: 14px;\"><strong style=\"font-size: 14px; font-weight: bold;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">英飞凌中国</span></strong></p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">英飞凌科技股份公司于</span>1995<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">年正式进入中国市场。自</span><span style=\"font-family: Arial; font-size: 14px;\">1995</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">年</span><span style=\"font-family: Arial; font-size: 14px;\">10</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">月在无锡建立第一家企业以来，英飞凌的业务取得非常迅速的增长，在中国拥有约</span><span style=\"font-family: Arial; font-size: 14px;\">2000</span><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">名员工，已经成为英飞凌亚太乃至全球业务发展的重要推动力。英飞凌在中国建立了涵盖研发、生产、销售、市场、技术支持等在内的完整的产业链，并在销售、技术研发、人才培养等方面与国内领先的企业、高等院校开展了深入的合作。</span></p><p style=\"font-size: 14px;\">&nbsp;</p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">更多信息请访问</span><a href=\"http://www.infineon.com\" style=\"font-size: 14px;\">www.infineon.com</a></p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">本新闻稿还可在</span><a href=\"http://www.infineon.com.cn\" style=\"font-size: 14px;\">www.infineon.com.cn</a>&nbsp;<span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">上找到。</span></p><p style=\"font-size: 14px;\"><span style=\"font-family: &quot;Source Han Sans SC&quot;; font-size: 14px;\">关注我们官方微信：英飞凌中国</span></p></div><p><br/></p>', '', null, '英飞凌', '', '2018-11-16 10:49:49', '0', '0', null, null, null, '0', null, '2018-11-16 10:49:49', '[]', null, null, '2017-03-15 15:15:00');
