INSERT INTO `WEB_TXT` VALUES ('0c95d9ddf7d447bd9542d29e7d886cdd', '<p><span style=\"font-size:14px;\">您在工作中使用英飞凌芯片时是否遇到一些问题?有的问题是否已经解决或者一些尚未解决？现都可以在英飞凌社区发帖与大家分享进行探讨<br/></span></p><p><span style=\"font-size:14px;\">【活动时间】：3月1日——3月13日 </span> </p><p><span style=\"font-size:14px;\">【参与形式】：</span> </p><p><span style=\"font-size:14px;\">■ 在活动期间，在<a href=\"http://www.infineon-power-rf.org/bbs/\"><span style=\"font-size:16px;color:#4C33E5;\"><strong>英飞凌社区</strong></span></a>发布2篇与英飞凌相关的问题帖（若已解决的问题帖，需</span><span style=\"font-size:14px;line-height:1.5;\">写出解决方法），即可获得20元话费</span> </p><p><span style=\"font-size:14px;\"><span style=\"color:#E53333;\">备注：问题帖不能是一句话提问，需将问题描述清楚，方便大家进行解答</span><br/></span> </p><p><br/></p><p><span style=\"font-size:14px;\"> ■ 在活动期间，参与讨论以下列出的问题帖（帖子不定时更改）或社区其他技术帖，完成<span style=\"font-size:14px;line-height:21px;\">回复数量10个以上</span>的网友即可获得20元话费</span> </p><p><span style=\"font-size:14px;line-height:21px;\">备注：1、必须在三天或者三天以上时间内完成</span> </p><p><span style=\"font-size:14px;line-height:21px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2、类似学习了，支持等回复不予以计算在内</span> </p><p><span style=\"font-size:14px;line-height:21px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;3、楼主回复自己的帖子也计算在内</span> </p><p><br/></p><p><span style=\"font-size:14px;\"><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/223998\"><span style=\"color:#4C33E5;\">1ED020I12F 问题请教</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/223914\"><span style=\"color:#4C33E5;\">D-CAP2™ 模式适合应用到英飞凌产品上吗</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/223940\"><span style=\"color:#4C33E5;\">QR中变压器如何设计保证最大效率</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224022\"><span style=\"color:#4C33E5;\">英飞凌有没有快充的电源芯片或者方案</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224025\"><span style=\"color:#4C33E5;\">英飞凌大功率快恢复二级管有哪些？</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224026\"><span style=\"color:#4C33E5;\"></span><span style=\"color:#4C33E5;\">哪位大神帮忙看看电路图中的MOS选择的型号是</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224027\"><span style=\"color:#4C33E5;\">开关管开通尖峰和负载电流尖峰怎么产生，怎么抑制？</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224033\"><span style=\"color:#4C33E5;\">ISO5500管子高压有尖峰干扰</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224032\"><span style=\"color:#4C33E5;\">英飞凌驱动电源测试疑问</span></a><br/><a href=\"http://www.infineon-power-rf.org/bbs/topic_show/id/224044\"><span style=\"color:#4C33E5;\">想寻找一款led电源控制ic 英飞凌有吗？</span></a><br/><br/></span> </p><p><span style=\"font-size:14px;\">获奖名单：</span> </p><p><span style=\"font-size:14px;line-height:21px;\">发布帖子获奖者：gxg1122、huanying、ljj1993、rws418456828、lovelee、fenglili、<br/> fengbin、dwwzl、elec921<br/></span><span style=\"font-size:14px;line-height:1.5;\">回帖获奖者：gxg1122、ljj1993、hellbaron2008、huzy、cool007、<br/> XIAOTU80、lovelee、aa20897、神采奕奕、flowerhuanghua、huanying、gaon<br/></span> </p><p><span style=\"font-size:14px;\"><br/></span> </p><p><span style=\"font-size:14px;\">请大家尽快与天津-恬恬 973919678<span style=\"font-size:14px;line-height:21px;\">联系</span></span> </p>', null);
