INSERT INTO `WEB_TXT` VALUES ('3eb50008f1fe44269a4c1b2630a11b87', '<p style=\"text-align:center;\"><strong><span style=\"font-size:24px;\">英飞凌电源管理与射频技术生态圈</span></strong> </p><p><span style=\"font-size:14px;\"> </span> </p><div style=\"margin:0px;padding:0px;color:#333333;font-family:Arial, &#39;微软雅黑&#39;;font-size:14px;background-color:#FFFFFF;\" align=\"center\"><a href=\"http://www.infineon-power-rf.org/\"><span style=\"font-size:16px;\"><strong>http://www.infineon-power-rf.org/</strong></span></a> </div><div style=\"margin:0px;padding:0px;color:#333333;font-family:Arial, &#39;微软雅黑&#39;;font-size:14px;background-color:#FFFFFF;\" align=\"center\"><span style=\"font-size:16px;\"><span style=\"font-size:16px;\"><strong>学技术~ 找方案 ~选芯片 ~寻服务</strong></span></span> </div><p><br/><span style=\"background-color:#FFFFFF;\">&nbsp; &nbsp;<span style=\"font-size:16px;\">英飞凌电源管理与射频生态圈鼓励创新，整合创意，努力吸引电源管理与射频领域具有创新性 和竞争力的解决方案；为终端客户提供完整的系统解决方案，用英飞凌最先进的半导体器件引领行 业发展。</span></span><br/><span style=\"font-size:16px;background-color:#FFFFFF;\">&nbsp; &nbsp;为了更直接了解工程师需求，满足更多工程的需要，请根据您的实际需要填写问卷，您的建议 很重要，我们将不断改进，完善社区内容，提供一个更专业的技术交流平台。</span><br/></p><p><span style=\"font-size:16px;\"><span style=\"font-size:16px;background-color:#FFFFFF;\">&nbsp; &nbsp;感谢您的配合！</span></span> </p><p><span style=\"font-size:16px;\"><span style=\"font-size:16px;background-color:#FFFFFF;\"><br/></span></span> </p><p><span style=\"font-size:14px;\"><strong><span style=\"font-size:16px;\">【活动时间】</span></strong><span style=\"font-size:16px;background-color:#FFFFFF;\">9月22日-10月20日</span><br/><span style=\"background-color:#FFFFFF;\"><strong><br/></strong></span></span> </p><p><span style=\"font-size:14px;\"><span style=\"font-size:16px;background-color:#FFFFFF;\"><strong>【活动内容】</strong></span><br/></span> </p><ul style=\"color:#333333;font-family:Arial, &#39;微软雅黑&#39;;font-size:14px;background-color:#FFFFFF;\"><li><p><span style=\"font-size:16px;\">→→→点击</span><span><strong><span style=\"font-size:24px;\">“</span></