INSERT INTO `WEB_TXT` VALUES ('63b7947a483d4fb5b26418e4bdcc0507', '<p style=\"text-indent: 2em;\">\r\n    专注于物联网人工智能服务，拥有完全自主知识产权；\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    拥有算法、计算能力、芯片能力全技术链条的语音人工智能企业；\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    连续两年入选福布斯“中国最快科技成长公司企业”，上榜科技部“中国独角兽企业”；\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    在“智慧生活+智慧服务”两大领域有成熟的商业落地，核心业务领跑行业；<br/>\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    家居业务：白色家电唯一规模量产芯片供应商\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    车载业务：中国后装车载语音方案市场占有70%\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    医疗业务：国内首家医疗云服务供应商\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    教育业务：社会化口语评测市场占有稳居第一\r\n</p>\r\n<p style=\"text-indent: 2em;\">\r\n    <br/>\r\n</p>\r\n<p style=\"text-indent: 0em;\">\r\n    <a href=\"/channel/join\" target=\"_blank\" title=\"加入我们\" _href=\"/channel/join\"><img src=\"/uploadFiles/image/20181024/1540370496163086040.png\" _src=\"/uploadFiles/image/20181024/1540370496163086040.png\" alt=\"INFIN_Associated_Partner_Signet_Update.png\"/></a>\r\n</p>', null);
