INSERT INTO `WEB_TXT` VALUES ('6d48c6ca454e4145bb605cd08a951494', '<p style=\";margin-top:0;margin-bottom:0;text-indent:0in;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align: baseline\">\r\n    <span style=\"font-size:16px;font-family:+mn-ea;color:black\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family: 微软雅黑, Microsoft YaHei;\"><span style=\"font-size: 16px; color: black;\">武汉情智感知科技有限公司是一家专注于人工智能系统、服务机器人系列（情感识别与交互系统</span><span style=\"font-size: 16px; color: black;\">+</span><span style=\"font-size: 16px; color: black;\">认知计算</span><span style=\"font-size: 16px; color: black;\">+</span><span style=\"font-size: 16px; color: black;\">可穿戴情感机器人</span><span style=\"font-size: 16px; color: black;\">+</span><span style=\"font-size: 16px; color: black;\">具有情感认知的移动云计算）研发的高新技术企业。公司成立于</span><span style=\"font-size: 16px; color: black;\">2018</span><span style=\"font-size: 16px; color: black;\">年</span><span style=\"font-size: 16px; color: black;\">5</span><span style=\"font-size: 16px; color: black;\">月，由华中科技大学博士生导师陈敏教授创办。研发团队依托于华中科技大学嵌入与普适计算实验室，团队现有青年教师</span><span style=\"font-size: 16px; color: black;\">1</span><span style=\"font-size: 16px; color: black;\">名，助理研究员</span><span style=\"font-size: 16px; color: black;\">1</span><span style=\"font-size: 16px; color: black;\">名，博士后</span><span style=\"font-size: 16px; color: black;\">2</span><span style=\"font-size: 16px; color: black;\">名，在读博士</span><span style=\"font-size: 16px; color: black;\">8</span><span style=\"font-size: 16px; color: black;\">名，在读硕士</span><span style=\"font-size: 16px; color: black;\">14</span><span style=\"font-size: 16px; color: black;\">名，团队从实验室成立起就专注于情感交互机器人、可穿戴情感机器人等研究，在该领域具有深厚的技术积累。公司立足高端人工智能的研究，用智能科技优化人类生活，做世界级服务机器人企业，以期在服务机器人领域具有世界级技术领先地位，并取得广阔的市场前景。</span></span>\r\n</p>\r\n<p style=\";margin-top:0;margin-bottom:0;text-indent:0in;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align: baseline\">\r\n    <span style=\"font-family: 微软雅黑, Microsoft YaHei;\"><span style=\"font-size: 16px; color: black;\"><br/></span></span>\r\n</p>\r\n<p style=\";margin-top:0;margin-bottom:0;text-indent:0in;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align: baseline\">\r\n    <span style=\"font-family: 微软雅黑, Microsoft YaHei;\"><span style=\"font-size: 16px; color: black;\"><br/></span></span>\r\n</p>\r\n<p style=\";margin-top:0;margin-bottom:0;text-indent:0in;text-align:left;direction:ltr;unicode-bidi:embed;vertical-align: baseline\">\r\n    <a href=\"/channel/join\" target=\"_blank\" title=\"加入我们\" _href=\"/channel/join\"><span style=\"font-family: 微软雅黑, Microsoft YaHei;\"><span style=\"font-size: 16px; color: black;\"><img src=\"/uploadFiles/image/20181024/1540370231484065131.png\" _src=\"/uploadFiles/image/20181024/1540370231484065131.png\" alt=\"INFIN_Associated_Partner_Signet_Update.png\"/></span></span></a>\r\n</p>', null);
