INSERT INTO `WEB_TXT` VALUES ('79800f2abe5b48de81390616ca0e9f6a', '<p style=\"line-height: 150%; margin-top: 0px; margin-bottom: 0px; text-align: left; direction: ltr; unicode-bidi: embed; vertical-align: baseline; text-indent: 2em;\">\r\n    <span style=\"font-family: 微软雅黑, Microsoft YaHei; color: black; font-size: 16px;\">上海长戈智能科技有限公司当前的毫米波雷达产品主要有24GHz和60GHz两大类，其中24GHz毫米波产品当前的应用有：室内照明感应和智能路灯控制、高速公路测速、交通流量实时监控、智能家居(开关/门铃等)、雷达物位计、汽车辅助驾驶等领域；60GHz毫米波雷达当前主要应用于人体呼吸/心跳监测。在我们重点关注的智能驾驶和无人驾驶领域，上海长戈智能还可提供毫米波雷达、激光雷达，GNSS高精度定位一体化解决产品，以及该领域相关的测试测量服务。</span>\r\n</p>\r\n<p>\r\n    <br/>\r\n</p>\r\n<p>\r\n    <a href=\"/channel/join\" target=\"_blank\" title=\"加入我们\" _href=\"/channel/join\"><img src=\"/uploadFiles/image/20181024/1540370274836058839.png\" _src=\"/uploadFiles/image/20181024/1540370274836058839.png\" alt=\"INFIN_Associated_Partner_Signet_Update.png\"/></a>\r\n</p>', null);
